import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import { Form, Button, Container, Card } from 'react-bootstrap';

function ProductForm() {
    const [product, setProduct] = useState({ name: '', price: '', stock: '', category: '' });
    const [categories, setCategories] = useState([]);
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        axios.get('http://localhost:3000/categories')
            .then(response => setCategories(response.data))
            .catch(error => console.error('There was an error fetching the categories!', error));

        if (id) {
            axios.get(`http://localhost:3000/products/${id}`)
                .then(response => setProduct(response.data))
                .catch(error => console.error('There was an error fetching the product!', error));
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct(prevProduct => ({
            ...prevProduct,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const method = id ? 'put' : 'post';
        const url = id ? `http://localhost:3000/products/${id}` : 'http://localhost:3000/products';

        axios[method](url, product)
            .then(() => navigate('/products'))
            .catch(error => console.error('There was an error saving the product!', error));
    };

    return (
        <Container>
            <Card>
                <Card.Body>
                    <Card.Title>{id ? 'Edit' : 'Add'} Product</Card.Title>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3">
                            <Form.Label>Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="name"
                                value={product.name}
                                onChange={handleChange}
                                placeholder="Enter product name"
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Price</Form.Label>
                            <Form.Control
                                type="number"
                                name="price"
                                value={product.price}
                                onChange={handleChange}
                                placeholder="Enter product price"
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Stock</Form.Label>
                            <Form.Control
                                type="number"
                                name="stock"
                                value={product.stock}
                                onChange={handleChange}
                                placeholder="Enter product stock"
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Category</Form.Label>
                            <Form.Control
                                as="select"
                                name="category"
                                value={product.category}
                                onChange={handleChange}
                            >
                                <option value="">Select a category</option>
                                {categories.map(category => (
                                    <option key={category._id} value={category._id}>
                                        {category.name}
                                    </option>
                                ))}
                            </Form.Control>
                        </Form.Group>
                        <Button variant="primary" type="submit">
                            Save
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
}

export default ProductForm;
