import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Form, Button, Container, Card } from 'react-bootstrap';

function SaleForm() {
    const [sale, setSale] = useState({ products: [], totalAmount: 0 });
    const [products, setProducts] = useState([]);
    const [selectedProduct, setSelectedProduct] = useState({ product: '', quantity: 0 });
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('http://localhost:3000/products')
            .then(response => setProducts(response.data))
            .catch(error => console.error('There was an error fetching the products!', error));
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSelectedProduct(prevProduct => ({
            ...prevProduct,
            [name]: value
        }));
    };

    const handleAddProduct = () => {
        setSale(prevSale => ({
            ...prevSale,
            products: [...prevSale.products, selectedProduct],
            totalAmount: prevSale.totalAmount + (selectedProduct.quantity * products.find(p => p._id === selectedProduct.product).price)
        }));
        setSelectedProduct({ product: '', quantity: 0 });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:3000/sales', sale)
            .then(() => navigate('/sales'))
            .catch(error => console.error('There was an error creating the sale!', error));
    };

    return (
        <Container>
            <Card>
                <Card.Body>
                    <Card.Title>Add Sale</Card.Title>
                    <Form onSubmit={handleSubmit}>
                        <Form.Group className="mb-3">
                            <Form.Label>Product</Form.Label>
                            <Form.Control
                                as="select"
                                name="product"
                                value={selectedProduct.product}
                                onChange={handleChange}
                            >
                                <option value="">Select a product</option>
                                {products.map(product => (
                                    <option key={product._id} value={product._id}>
                                        {product.name}
                                    </option>
                                ))}
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Quantity</Form.Label>
                            <Form.Control
                                type="number"
                                name="quantity"
                                value={selectedProduct.quantity}
                                onChange={handleChange}
                                placeholder="Enter quantity"
                            />
                        </Form.Group>
                        <Button variant="primary" onClick={handleAddProduct}>
                            Add Product
                        </Button>
                        <Button variant="primary" type="submit">
                            Save
                        </Button>
                    </Form>
                </Card.Body>
            </Card>
        </Container>
    );
}

export default SaleForm;
